DECLARE
v_newVal NUMBER(12) := 0;
v_incval NUMBER(12) := 0;
BEGIN
  IF INSERTING AND :new.product_id IS NULL THEN
SELECT  iot_product_product_id_SEQ.NEXTVAL INTO v_newVal FROM DUAL;
