DROP TABLE IF EXISTS "sys_user";
CREATE TABLE "sys_user" (
  "user_id" int8 GENERATED BY DEFAULT AS IDENTITY (START WITH 9 INCREMENT BY 1),
  "dept_id" int8,
  "user_name" varchar(30) COLLATE "pg_catalog"."default" NOT NULL,
  "nick_name" varchar(30) COLLATE "pg_catalog"."default" NOT NULL,
  "user_type" varchar(2) COLLATE "pg_catalog"."default" DEFAULT '00',
  "email" varchar(50) COLLATE "pg_catalog"."default" DEFAULT '',
  "phonenumber" varchar(11) COLLATE "pg_catalog"."default" DEFAULT '',
  "sex" char(1) COLLATE "pg_catalog"."default" DEFAULT '0',
  "avatar" varchar(200) COLLATE "pg_catalog"."default" DEFAULT '',
  "password" varchar(100) COLLATE "pg_catalog"."default" DEFAULT '',
  "status" int2 DEFAULT 0,
  "del_flag" int2 DEFAULT 0,
  "login_ip" varchar(128) COLLATE "pg_catalog"."default" DEFAULT '',
  "login_date" timestamp(6),
  "create_by" varchar(64) COLLATE "pg_catalog"."default" DEFAULT '',
  "create_time" timestamp(6),
  "update_by" varchar(64) COLLATE "pg_catalog"."default" DEFAULT '',
  "update_time" timestamp(6),
  "remark" varchar(500) COLLATE "pg_catalog"."default"
)
;
COMMENT ON COLUMN "sys_user"."user_id" IS '用户ID';
COMMENT ON COLUMN "sys_user"."dept_id" IS '部门ID';
COMMENT ON COLUMN "sys_user"."user_name" IS '用户账号';
COMMENT ON COLUMN "sys_user"."nick_name" IS '用户昵称';
COMMENT ON COLUMN "sys_user"."user_type" IS '用户类型（00系统用户）';
COMMENT ON COLUMN "sys_user"."email" IS '用户邮箱';
COMMENT ON COLUMN "sys_user"."phonenumber" IS '手机号码';
COMMENT ON COLUMN "sys_user"."sex" IS '用户性别（0男 1女 2未知）';
COMMENT ON COLUMN "sys_user"."avatar" IS '头像地址';
