    INSERT INTO [dbo].[news] ([news_id], [title], [content], [img_url], [is_top], [is_banner], [category_id], [category_name], [status], [author], [del_flag], [create_by], [create_time], [update_by], [update_time], [remark]) VALUES (N'4', N'物美智能-快速搭建物联网和智能家居平台', N'<p class="ql-align-justify">物美智能 wumei-smart 是一个简单易用的生活物联网平台。可用于搭建物联网平台以及二次开发和学习。设备接入使用EMQX消息服务器，加密认证；后端采用Spring boot；前端采用Vue；移动端采用Uniapp；数据库采用Mysql和Redis；设备端支持ESP32、ESP8266、树莓派等；</p><p class="ql-align-justify"><img src="/prod-api/profile/upload/2022/05/11/5f479f25-b85d-4c9f-b6a7-deadd2cdec76.png"></p><p class="ql-align-justify"><br></p><p class="ql-align-justify"><strong>系统功能介绍</strong></p><p class="ql-align-justify"><br></p><p class="ql-align-justify">1.权限管理： 用户管理、部门管理、岗位管理、菜单管理、角色管理、字典和参数管理等</p><p class="ql-align-justify"><br></p><p class="ql-align-justify">2.系统监控： 操作日志、登录日志、系统日志、在线用户、服务监控、连接池监控、缓存监控等</p><p class="ql-align-justify"><br></p><p class="ql-align-justify">3.产品管理： 产品、产品物模型、产品分类、产品固件、授权码等</p><p class="ql-align-justify"><br></p><p class="ql-align-justify">4.设备管理： 控制、分组、定时、日志、统计、定位、OTA升级、影子模式、实时监测、加密认证等</p><p class="ql-align-justify"><br></p><p class="ql-align-justify">5.EMQ管理： Mqtt客户端、监听器、消息主题、消息订阅、插件管理、规则引擎、资源</p><p class="ql-align-justify"><br></p><p class="ql-align-justify">6.硬件 SDK： 支持WIFI和MQTT连接、物模型响应、实时监测、定时上报监测数据、AES加密、NTP时间等</p><p class="ql-align-justify"><br></p><p class="ql-align-justify">7.物模型管理： 属性（设备状态和监测数据），功能（执行特定任务），事件（设备主动上报给云端）</p><p class="ql-align-justify"><br></p><p class="ql-align-justify">8.其他（开发中）：第三方登录，设备分享、设备告警、场景联动（进度50%），智能音箱、多租户、APP界面自定义（进度40%），时序数据库、分布式集群部署、Granfa监控（进度30%），视频流处理、桌面端模拟器/监控、安卓端模拟器/监控（进度20%）</p><p class="ql-align-justify"><strong>﻿</strong></p><p class="ql-align-justify"><br></p><p class="ql-align-justify"><strong>硬件设备接入流程</strong></p><p><br></p><p>1.设备认证：加密认证、简单认证和emqx支持的多种认证方式。</p><p class="ql-align-justify">2.设备交互：发布和订阅物模型、设备信息、设备升级和时钟同步等mqtt主题</p>', N'/iot/tool/download?fileName=/profile/iot/1/2022-0508-133031.png', N'1', N'0', N'2', N'相关产品', N'1', N'物美智能', N'0', N'', N'2022-05-12 12:13:46.0000000', N'', N'2022-05-12 12:13:46.0000000', N'物美智能')
